sourcesContent:
import db from '../../../databases/models'
import {Op} from 'sequelize'   // Op  operation     

//   Access Database 
const ProductRepository = {
    findAll: async () => await db.Products.findAll({  // 
        order: [    //  id
            ['id', 'DESC']
        ]
    }),
    findOneById: async (id) => await db.Products.findByPk(id), //  PK
    findByPrice: async (min, max) => await db.Products.findAll({ // 
        where: {    // 
            price: {
                [Op.gte]: min, // gte  >=
                [Op.lte]: max  // lte  <=
            }
        }
    }),
    create: async (product) => await db.Products.create(product),
    updateById: async (id, product) => await db.Products.update(product, {
        where: {
            id: id
        }
    }),
    deleteById: async (id) => await db.Products.destroy({
        where: {
            id: id
        }
    })
}

export default ProductRepository

