sourcesContent:
import multer from "multer" // package  upload file
import fs from "fs" // fs  file system

// keyUpload  upload  key  Model 
export const keyUpload = "image"

export const multerConfig = {
    storage: multer.diskStorage({
        //  file
        destination: (req, file, next) => { // next  callback 
            const folder = './images/'
            if(!fs.existsSync(folder)) { //  lib fs  folder  
                fs.mkdirSync(folder)
            }
            next(null, folder)
        },
        // 
        filename: (req, file, next) => {
            const type = file.mimetype.split('/')[1] //  type  file
            const uniqueSuffix = Date.now() + '-' + Math.round(Math.random() * 1E9) // 
            next(null, `${file.fieldname}-${uniqueSuffix}.${type}`)  // ${file.fieldname}-${uniqueSuffix}.${type}   xxxxx45454.jpg
        }
    }),
    // 
    limits: {
        fieldSize: 1024 * 1024 * 5, // 5 MB
    },
    // 
    fileFilter: (req, file, next) => {
        const typeArray = file.mimetype.split('/') //  multipart/form-data  image 
        const fileType = typeArray[1]

        if(fileType !== 'png' && fileType !== 'jpg' && fileType !== 'gif' && fileType !== 'jpeg') {
            next({message: `File type not supported ${fileType}`}, false)
        }else{
            next(null, true)
        }
    }
}

