sourcesContent:
import AccountRepository from '../repositories/account.repository.js'
import bcrypt from 'bcrypt'
import jwt from '../../../configs/jwt.js'

//  business logic  Access  ProductRepository
const AccountService = {
    register: async (account) => {
        account.password = await bcrypt.hash(account.password, 8) // hash  8  salt  string  int 
        return await AccountRepository.create(account)
    },
    login: async (username, password) => {
        const result = await AccountRepository.findByUsername(username)
        if(result && await bcrypt.compare(password, result.password)) { // bcrypt.compare  password 
            const payload = { // data  token
                sub: result.username, // subject 
                role: result.role,
                addtional: "todo",
                addtional1: "todo1"
            }
            return jwt.generateToken(payload) //  token  return  login 
        }

        return
    }
}

export default AccountService

