sourcesContent:
import db from '../../../databases/models'

//   Access Database 
const AccountRepository = {
    create: async (account) => await db.Accounts.create(account),
    findByUsername: async (username) => await db.Accounts.findOne({
        where: {
            username
        }
    })
}

export default AccountRepository

