sourcesContent:
import jwt from 'jsonwebtoken'
import path from 'path'
import fs from "fs"

//  key 
const privateKey = fs.readFileSync(path.join(__dirname, "../../s4t", "private.key"))
const publicKey = fs.readFileSync(path.join(__dirname, "../../s4t", "public.key"))

// config jwt
const signOptions = {
    issuer: 'youtube', //  token
    audience: 'www.youtube.com', //  token
    algorithm: 'RS256'
}

const generateToken = (payload) => jwt.sign( //  jwt
    payload,  // data jwt
    privateKey,  //  private key
    {
        ...signOptions,  // config jwt
        expiresIn: '30d' //  token
    }
)

// verifyToken  middleware  route
const verifyToken = (req, res, next) => {
    const authHeader = req.headers['authorization'] //  header authorization  req 
    const token = authHeader && authHeader.split(' ')[1] //  header authorization  authorization: bearer xxxxx 
    if (!token) { //  token  return void  status 401
        res.status(401).json()
        return
    }

    jwt.verify(
        token, publicKey, //  token  privateKey  publicKey 
        signOptions, // verify config jwt
        (error, decode) => { // decode   payload
            if (error) { //  error  verify token
                res.status(401).json()
                return
            }

            // decode jwt payload  header  req  route  controller 
            req.sub = decode.sub
            req.role = decode.role

            next() // 
        }
    )
}

export default {
    generateToken,
    verifyToken
}
